INSERT INTO `product_detail` VALUES (58, 'Mang trọn thiết kế đầy đẳng cấp', 'Sáng bừng đêm tới với camera kép AI', 'Hệ điều hành hoàn toàn mới', 'Điện Thoại Vsmart Joy 2+ (Hàng Chính Hãng) có thiết kế màn hình giọt nước thời thượng cùng mặt lưng sống động, sắc màu. Với phản xạ ánh sáng cho mỗi góc nhìn một màu sắc khác nhau.Vsmart Joy 2+ cũng là sản phẩm có sức mạnh vượt trội trong phân khúc do sử dụng bộ xử lý Snapdragon 450 từ Qualcomm - nhà sản xuất Chip hàng đầu thế giới. Snapdragon 450 mạnh hơn 25% so với thế hệ chip cũ trong khi tiết kiệm 30% năng lượng khi chơi game. Kết hợp với màn hình lớn 6,2” HD+ sắc nét, Vsmart Joy2+  hứa hẹn sẽ mang lại trải nghiệm sử dụng hoàn hảo cho khách hàng.', 'Về Camera, Joy 2+ tiếp tục kế thừa những tính năng camera cao cấp trên Vsmart Live: camera tự động sử dụng trí tuệ nhân tạo để chọn những chế độ chụp phù hợp nhất với điều kiện môi trường xung quanh, chế độ xóa phông ảo diệu với cụm camera 13MP + 5MP chất lượng cao, hiệu ứng làm đẹp và chế độ AR Sticker ghép mặt thú vị dành cho giới trẻ.', 'Joy 2+ tiếp tục được sử dụng VOS 2.0, hệ điều hành hoàn toàn mới được các chuyên gia và kỹ sư phần mềm Vsmart nghiên cứu. Cụ thể, VOS 2.0 của Vsmart tái thiết kế toàn bộ giao diện, tối ưu hóa thao tác sử dụng của người dùng như thao tác điều khiển bằng cử chỉ, không sử dụng thanh điều hướng, sao lưu, khôi phục và đồng bộ dữ liệu người dùng. VOS2.0 của Vsmart cũng có thêm chế độ đơn giản cho người lớn tuổi và có tính năng sử dụng hai tài khoản trên cùng một ứng dụng.', 'img/sanpham/vsmart/joy/mota/1.jpg', 'img/sanpham/vsmart/joy/mota/2.jpg', 'img/sanpham/vsmart/joy/mota/3.jpg', '1');
INSERT INTO `product_detail` VALUES (59, 'Màn hình tràn viền IPS LCD 5.45\"\r\n', 'Khả năng kháng nước an toàn\r\n', 'Hiệu năng tốt với chip Qualcomm Snapdragon 439\r\n', 'Điện Thoại Xiaomi Redmi 7A (2GB/16GB) sở hữu vẻ ngoài khá thời trang với một màn hình tràn viền IPS LCD 5.45 inch ở mặt trước. Đặc biệt với thiết kế màn hình có độ sáng khá tốt giúp người tiêu dùng có thể dễ dàng sử dụng ngoài trời. Bên cạnh đó máy còn được tích hợp công nghệ lọc ánh sáng xanh gây hại được phát ra từ màn hình rất đáng khen.\r\n\r\n', 'Máy cũng có khả năng \"kháng nước nhẹ\" khi mặt lưng được phủ một lớp nano P2i còn các chi tiết khác như jack cắm tai nghe, lỗ cắm sạc cũng được bảo vệ bằng lớp vỏ bọc cao su để nước không thấm vào bên trong.\r\n\r\n', 'Xiaomi Redmi 7A được cung cấp sức mạnh bởi con chip Qualcomm Snapdragon 439 8 nhân 64-bit cùng với đó là 2 GB RAM và 16 GB bộ nhớ trong. Máy phản hồi khá nhanh các thao tác hằng ngày của người dùng từ lướt facebook, xem YouTube tới chơi một số game nhẹ. Ngoài ra chiếc smartphone này cũng hỗ trợ thẻ nhớ với dung lượng tối đa lên tới 512 GB thoải mái cho bạn lưu trữ dữ liệu.\r\n\r\n', 'img/sanpham/xiaomi/redminote7/mota/1.jpg', 'img/sanpham/xiaomi/redminote7/mota/2.jpg', 'img/sanpham/xiaomi/redminote7/mota/3.jpg', '1');
