INSERT INTO `product_detail` VALUES (71, 'Camera đáp ứng tốt nhu cầu\r\n', 'Thiết kế đơn giản, nhỏ gọn\r\nThiết kế đơn giản, nhỏ gọn\r\n', 'Thời lượng pin tốt cho cả ngày\r\n', 'sở hữu camera chính có độ phân giải 8 MP với giao diện chụp ảnh đơn giản, dễ dàng làm quen và sử dụng. Máy cho khả năng lấy nét khá nhanh, chi tiết chụp lại cũng khá ổn với điều kiện đầy đủ sáng.Khả năng quay phim cũng đạt chất lượng HD+ mang lại những đoạn video sắc nét để bạn có thể chia sẻ với bạn bè. Camera selfie 5 MP mang lại những đoạn video call vui vẻ hay chụp lại những bức ảnh tự sướng với mọi người xung quanh.', 'Vsmart Bee sở hữu thiết kế không quá cầu kỳ với một thân hình khá nhỏ gọn và dễ cầm nắm. Máy có màn hình kích thước 5.45 inch vừa đủ để người dùng có thể trải nghiệm thông tin giải trí đa phương tiện trên máy.', 'Vsmart Bee được nhà sản xuất trang bị cho viên pin có dung lượng 2500 mAh mang lại thời gian sử dụng khá tốt. Tuy dung lượng pin không quá lớn nhưng nhờ sở hữu một màn hình vừa phải nên máy vẫn cho thời gian sử dụng pin tốt trong tầm giá.\r\n\r\n', 'img/sanpham/vsmart/bee/mota/1.jpg', 'img/sanpham/vsmart/bee/mota/2.jpg', 'img/sanpham/vsmart/bee/mota/3.jpg', '1');
INSERT INTO `product_detail` VALUES (72, 'Camera đáp ứng tốt nhu cầu\r\n', 'Thiết kế đơn giản, nhỏ gọn\r\nThiết kế đơn giản, nhỏ gọn\r\n', 'Thời lượng pin tốt cho cả ngày\r\n', 'sở hữu camera chính có độ phân giải 8 MP với giao diện chụp ảnh đơn giản, dễ dàng làm quen và sử dụng. Máy cho khả năng lấy nét khá nhanh, chi tiết chụp lại cũng khá ổn với điều kiện đầy đủ sáng.Khả năng quay phim cũng đạt chất lượng HD+ mang lại những đoạn video sắc nét để bạn có thể chia sẻ với bạn bè. Camera selfie 5 MP mang lại những đoạn video call vui vẻ hay chụp lại những bức ảnh tự sướng với mọi người xung quanh.', 'Vsmart Bee sở hữu thiết kế không quá cầu kỳ với một thân hình khá nhỏ gọn và dễ cầm nắm. Máy có màn hình kích thước 5.45 inch vừa đủ để người dùng có thể trải nghiệm thông tin giải trí đa phương tiện trên máy.', 'Vsmart Bee được nhà sản xuất trang bị cho viên pin có dung lượng 2500 mAh mang lại thời gian sử dụng khá tốt. Tuy dung lượng pin không quá lớn nhưng nhờ sở hữu một màn hình vừa phải nên máy vẫn cho thời gian sử dụng pin tốt trong tầm giá.\r\n\r\n', 'img/sanpham/vsmart/bee/mota/1.jpg', 'img/sanpham/vsmart/bee/mota/2.jpg', 'img/sanpham/vsmart/bee/mota/3.jpg', '1');
INSERT INTO `product_detail` VALUES (73, 'Tổng quan sản phẩm\r\n', 'Thiết bị có 3 tùy chọn màu sắc cho người dùng lựa chọn\r\n', 'Màn hình Infinity-U kích thước lớn\r\n', 'Điện Thoại Samsung Galaxy A70 (128GB/6GB) - Hàng Chính Hãng (Đã Kích Hoạt) Bảo Hành 12 Tháng sản phẩm vẫn được làm bằng chất liệu nhựa giả thủy tinh nhưng là nhựa cao cấp với tên gọi 3D Graffitistic, mang đến sự cứng cáp và chắc chắn khi cầm nắm.\r\n\r\n', 'Vị trí của nút tăng giảm âm lượng và nút nguồn không thay đổi, bao gồm cả thẻ nhớ và khay SIM cũng tương tự. Đồng thời, Galaxy A70 còn được trang bị cổng kết nối USB-C hiện đại và jack cắm tai nghe 3.5mm truyền thống vẫn được giữ lại.\r\n\r\n', 'Galaxy A70 được trang bị màn hình giọt nước có kích thước lên tới 6.7 inch cùng độ phân giải đạt Full HD+, so với A50 thì màn hình này lớn hơn một chút. Bên cạnh đó, thiết bị vẫn sử dụng tấm nền Super AMOLED mang đến chất lượng hiển thị màu sắc rực rỡ, chân thực, sắc nét.\r\n\r\n', 'img/sanpham/samsung/a70/mota/1.jpg', 'img/sanpham/samsung/a70/mota/2.jpg', 'img/sanpham/samsung/a70/mota/3.jpg', '1');
